import React from "react"
import FormInput from "../form-input/form-input.component"
import CustomButton from "../custom-button/custom-button.component"
import { auth, createUserProfileDocument } from "../../firebase/firebase.utils"
import "./sign-up.styles.scss"

class SignUp extends React.Component {
  constructor() {
    super()

    this.state = {
      displayName: "",
      email: "",
      password: "",
      confirmPassword: "",
    }
  }

  handleSubmit = async (event) => {
    event.preventDefault()

    const { displayName, email, password, confirmPassword } = this.state
    console.log(displayName)
    console.log(email)
    console.log(password)
    console.log(confirmPassword)

    if (password !== confirmPassword) {
      alert("Die beiden Passworte stimmen nicht überein.")
      console.log("Password not same")
      return
    }

    console.log("now the try/catch")
    try {
      const { user } = await auth.createUserWithEmailAndPassword(
        email,
        password
      )
      console.log(user)
      /**
       * by adding the displayName as an object {displayName}
       * it creates a key: value pair out of it
       */
      createUserProfileDocument(user, { displayName })

      console.log("clear the form")
      // this will clear the form
      this.state({
        displayName: "",
        email: "",
        password: "",
        confirmPassword: "",
      })
    } catch (error) {
      console.log(error)
    }
  }

  handleChange = (event) => {
    const { name, value } = event.target

    this.setState({ [name]: value })
  }

  render() {
    const { displayName, email, password, confirmPassword } = this.state
    return (
      <div className="sign-up">
        <h2 className="title">Ich habe noch kein Konto</h2>
        <span>Mit Email und Passwort anmelden</span>
        <form className="sign-up-form" onSubmit={this.handleSumbit}>
          <FormInput
            type="text"
            name="displayName"
            value={displayName}
            label="Benutzername"
            onChange={this.handleChange}
            required
          />
          <FormInput
            type="email"
            name="email"
            value={email}
            label="Email"
            onChange={this.handleChange}
            required
          />
          <FormInput
            type="password"
            name="password"
            value={password}
            label="Passwort"
            onChange={this.handleChange}
            required
          />
          <FormInput
            type="password"
            name="confirmPassword"
            value={confirmPassword}
            label="Passwort wiederholen"
            onChange={this.handleChange}
            required
          />
          <CustomButton type="submit">ANMELDEN</CustomButton>
        </form>
      </div>
    )
  }
}

export default SignUp
