import React from "react"
import { Route } from "react-router-dom"
import { createStructuredSelector } from "reselect"
import { connect } from "react-redux"

import { fetchCollectionsStartAsync } from "../../redux/shop/shop.actions"
import {
  isCollectionsFetchingSelector,
  isCollectionsLoadedSelector,
} from "../../redux/shop/shop.selector"

import WithSpinner from "../../components/with-spinner/with.spinner.component"

import CollectionsOverview from "../../components/collections-overview/collections-overview.component"
import Collection from "../collection/collection.component"

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview)
const CollectionWithSpinner = WithSpinner(Collection)

/**
 * we have access to the match property because in App.js
 * the ShopPage is <Route path="/shop" component={ShopPage} /> passed as
 * a Component in a Route and automatically Route passes match / history / location as
 * properties to its component
 * @param {*} param0
 */
class ShopPage extends React.Component {
  componentDidMount() {
    const { fetchCollectionsStartAsync } = this.props
    fetchCollectionsStartAsync()
  }

  // console.log(match)
  render() {
    const { match, isCollectionFetching, isCollectionsLoaded } = this.props

    return (
      <div className="shop-page">
        <Route
          exact
          path={`${match.path}`}
          render={(props) => (
            <CollectionsOverviewWithSpinner
              isLoading={isCollectionFetching}
              {...props}
            />
          )}
        />
        <Route
          path={`${match.path}/:collectionId`}
          render={(props) => (
            <CollectionWithSpinner
              isLoading={!isCollectionsLoaded}
              {...props}
            />
          )}
        />
      </div>
    )
  }
}

const mapStateToProps = createStructuredSelector({
  isFetchingCollections: isCollectionsFetchingSelector,
  isCollectionsLoaded: isCollectionsLoadedSelector,
})

const mapDispatchToProps = (dispatch) => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync()),
})

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage)
