import { createSelector } from "reselect";
import memoize from "lodash.memoize";
const COLLECTION_ID_MAP = {
  konzert: 1000,
  flamenco: 2000,
  kinder: 3000,
  "western-gitarren": 4000,
  meister: 5000
};

const shopSelector = state => state.shop;

export const collectionsSelector = createSelector([shopSelector], shop => shop.collections);
/**
 * collectionUrlParam is a string i.e. 'western'
 * and we try to find the matching collection ID with the
 * find() function, where we get back the collection that the collection.id
 * macthes the numeric value of the collectionUrlParam
 * @param {*} collectionUrlParam
 */

export const collectionSelector = memoize(collectionUrlParam => createSelector([collectionsSelector], collections => collections.find(collection => collection.id === COLLECTION_ID_MAP[collectionUrlParam])));
