import React from "react"
import { connect } from "react-redux"

import CollectionItem from "../../components/collection-item/collection-item.component"

import {
  collectionSelector,
  collectionsSelector,
} from "../../redux/shop/shop.selector"

import "./collection.styles.scss"

const CategoryPage = ({ match }) => {
  console.log(match)
  return (
    <div className="category">
      <h2>CATEGORY PAGE</h2>
    </div>
  )
}

/**
 * first parameter is the 'state' which is the overall state of the rootReducer
 * second parameter is the 'ownProps' which are all the props that we are wrapping
 * in this current component
 */
const mapStateToProps = (state, ownProps) => ({
  /**
   * (state) at the end of the function is necessary because unlike other selectors,
   * this selector needs a part of the state depending on the URL parameter
   */
  collection: collectionsSelector(ownProps.match.params.collectionId)(state),
})

export default connect(mapStateToProps)(CategoryPage)
