import { createSelector } from "reselect"

const COLLECTION_ID_MAP = {
  konzert: 1000,
  flamenco: 2000,
  kinder: 3000,
  western: 4000,
  meister: 5000,
}

const shopSelector = (state) => state.shop

export const shopCollectionSelector = createSelector(
  [shopSelector],
  (shop) => shop.collections
)
